import React from 'react';
import { Link } from 'react-router-dom';
import '../../App.scss';
import './Title.scss';

export default function Title() {  
  return (

    // <div className="App">
    //   <h1 className="title">You Don't Know Diddly Squat</h1>
    //   <h3 className="title__sub">A multiplayer quiz show for the whole family!</h3>
    //   <Link to="/join"><button className="button">Let's Go!</button></Link>
    // </div>

    <div>
    <h1>Speech synthesiser</h1>
    <p>Enter some text in the input below and press return  or the "play" button to hear it. change voices using the dropdown menu.</p>

    <form>
      <input type="text" className="txt"></input>
      <div>
        <label for="rate">Rate</label><input type="range" min="0.5" max="2" value="1" step="0.1" id="rate"></input>
        <div className="rate-value">1</div>
        <div className="clearfix"></div>
      </div>
        <div>
          <label for="pitch">Pitch</label><input type="range" min="0" max="2" value="1" step="0.1" id="pitch"></input>
          <div className="pitch-value">1</div>
          <div className="clearfix"></div>
        </div>
        <select>
        </select>
        <div className="ctls"><button id="play" type="submit">Play</button></div>
      </form>
    </div>
  )
}

// TTS

var synth = window.speechSynthesis;

var inputForm = document.querySelector('form');
var inputTxt = document.querySelector('.txt');
var voiceSelect = document.querySelector('select');

var pitch = document.querySelector('#pitch');
var pitchValue = document.querySelector('.pitch-value');
var rate = document.querySelector('#rate');
var rateValue = document.querySelector('.rate-value');

var voices = [];

function populateVoiceList() {
  voices = synth.getVoices().sort(function (a, b) {
      const aname = a.name.toUpperCase(), bname = b.name.toUpperCase();
      if ( aname < bname ) return -1;
      else if ( aname == bname ) return 0;
      else return +1;
  });
  var selectedIndex = 0;
  // console.log(voiceSelect.selectedIndex)
  // if (voiceSelect.selectedIndex !== null) {
  //   selectedIndex = voiceSelect.selectedIndex < 0 ? 0 : voiceSelect.selectedIndex;
  // }
  voiceSelect.innerHTML = '';
  for(let i = 0; i < voices.length ; i++) {
    var option = document.createElement('option');
    option.textContent = voices[i].name + ' (' + voices[i].lang + ')';

    if(voices[i].default) {
      option.textContent += ' -- DEFAULT';
    }

    option.setAttribute('data-lang', voices[i].lang);
    option.setAttribute('data-name', voices[i].name);
    voiceSelect.appendChild(option);
  }
  voiceSelect.selectedIndex = selectedIndex;
}

populateVoiceList();
if (speechSynthesis.onvoiceschanged !== undefined) {
  speechSynthesis.onvoiceschanged = populateVoiceList;
}

function speak(){
    if (synth.speaking) {
        console.error('speechSynthesis.speaking');
        return;
    }
    if (inputTxt.value !== '') {
    var utterThis = new SpeechSynthesisUtterance(inputTxt.value);
    utterThis.onend = function (event) {
        console.log('SpeechSynthesisUtterance.onend');
    }
    utterThis.onerror = function (event) {
        console.error('SpeechSynthesisUtterance.onerror');
    }
    var selectedOption = voiceSelect.selectedOptions[0].getAttribute('data-name');
    for(let i = 0; i < voices.length ; i++) {
      if(voices[i].name === selectedOption) {
        utterThis.voice = voices[i];
        break;
      }
    }
    utterThis.pitch = pitch.value;
    utterThis.rate = rate.value;
    synth.speak(utterThis);
  }
}

inputForm.onsubmit = function(event) {
  event.preventDefault();

  speak();

  inputTxt.blur();
}

pitch.onchange = function() {
  pitchValue.textContent = pitch.value;
}

rate.onchange = function() {
  rateValue.textContent = rate.value;
}

voiceSelect.onchange = function(){
  speak();
}
