import React, { useState, useEffect } from 'react';
import { useSpeechSynthesis } from 'react-speech-kit';
import { Link } from 'react-router-dom';
import LogoImage from "../../assets/Logo/LimboStudios.svg"
import '../../App.scss';
import './Logo.scss';

export default function Logo() {
  const [text, setText] = useState('Limbo Productions Presents');
  const { speak, voices } = useSpeechSynthesis();
  const voice = voices[51];

  const fadeOut = (item) => {
    const fadeTarget = document.querySelector(item)
    fadeTarget.classList.add("fade-out");
    fadeTarget.style.opacity = '0'
  }

  return (
      <div className="App">
        <img className="logo-image" src={LogoImage} alt="Limbo Productions Logo"/>        
        <Link to="/">
          <button className="button2" onClick={() => {speak({ text, voice }), fadeOut(".logo-image", ".button2")}}>Click to start</button>
          {/* {document.onclick = (
            speak({text, voice}),
            fadeOut(".logo-image")
          )} */}
        </Link>

      </div>

  )
}
