import React from 'react';
import { useSpeechSynthesis } from 'react-speech-kit';
import { Link } from 'react-router-dom';
import '../../App.scss';
import './QuestionIntro.scss';

export default function QuestionIntro() {  
  const [text, setText] = useState('Question number 1');
  const onEnd = () => {
    return (
      <Link to="/title" component={Title}/>
    )
  };
  const { speak, voices } = useSpeechSynthesis({onEnd});
  const voice = voices[51];
  return (
    <div className="App">
      <h1 className="question-intro__title">Question 1</h1>
      <Link to="/questions">
        <button className="button2" onClick={() => {
          speak({ text, voice })
          fadeOut(".logo-image")
          fadeOut(".button2")
        }}>Let's go</button>
      </Link>
    </div>
  );
}
