'use strict';

const _ = require('lodash');

module.exports = {
  requires: {},

  resetRequires () {
    var self = this;
    self.requires = {};
  },

  getRequires () {
    var self = this;
    return _
      .keys(self.requires)
      .map(filename => self.requires[filename])
      .map(data => _.keys(data))
      .reduce((arr, s) => arr.concat(s), []);
  },
};
