'use strict';

const _ = require('lodash');
const path = require('path');
const Data = require('./data');
const bank = require('./bank');

module.exports = {

  bank,
  Data,

  wrap (data = {}, opts = {}) {
    var self = this,
      item = new Data(data, opts);
    return item;
  },

  load (source, opts, name) {
    var self = this,
      item = self.wrap();
    return item.load(source, opts, name);
  },

  loadFile (filename, opts, name) {
    var self = this,
      item = self.wrap();
    return item.loadFile(filename, opts, name);
  },

};
