import './proto';
import kit from '../index';
import common from '../common';
import net from './net';
import socket from './socket';
import events from './events';

shareAll();

export default Object.assign(kit, common, {
  net, socket, events,
  shareAll,
});

function shareAll() {
  window.doc = window.document;
  window.body = window.doc.body;
  window.kit = kit;
  window._ = window._ || kit;
}
