'use strict';

Object.assign(Element.prototype, {

  enable (name) {
    var self = this;
    self.classList.add(`--${name}`);
    return self;
  },

  disable (name) {
    var self = this;
    self.classList.remove(`--${name}`);
    return self;
  },

  is (name) {
    var self = this;
    return self.classList.contains(`--${name}`);
  },

  toggle (name) {
    var self = this;
    self.is(name) ? self.disable(name) : self.enable(name);
    return self;
  },

});
