'use strict';

function getFieldsNames(Class) {
  var item = new Class();
  return Object.getOwnPropertyNames(item);
}

function getMethodsNames(Class) {
  return Object
    .getOwnPropertyNames(Class.prototype)
    .filter(name => ![
      'constructor',
      'toString',
      'valueOf',
    ].includes(name));
}

function getStaticNames(Class) {
  return Object
    .getOwnPropertyNames(Class)
    .filter(name => ![].includes(name));
}

function getStaticMethodsNames(Class) {
  return getStaticNames(Class).filter(name => typeof Class[name] === 'function');
}

function getMethods(Class) {
  var methods = {};
  getMethodsNames(Class).forEach(name => {
    methods[name] = Class.prototype[name];
  });
  return methods;
}

module.exports = {
  getStaticNames,
  getStaticMethodsNames,
  getMethodsNames,
  getFieldsNames,
  getMethods,
};
