
function filesize(val) {
  if (val < 1024) {
    return `${val} B`;
  }
  val = Math.round(val / 1024);
  if (val < 1024) {
    return `${val} KB`;
  }
  val = Math.round(val / 1024);
  if (val < 1024) {
    return `${val} MB`;
  }
  val = Math.round(val / 1024);
  if (val < 1024) {
    return `${val} GB`;
  }
  val = Math.round(val / 1024);
  if (val < 1024) {
    return `${val} TB`;
  }
  val = Math.round(val / 1024);
  if (val < 1024) {
    return `${val} PB`;
  }
}

module.exports = filesize;
