'use strict';

function random(start = 0, finish = 1) {
  if (arguments.length === 1) {
    finish = start;
    start = 0;
  }
  return Math.random() * finish - start;
}

function int (...args) {
  return Math.floor(random(...args));
}

function item (arr) {
  var index = int(arr.length);
  return arr[index];
}

module.exports = Object.assign(random, { int, item });
