'use strict';

function trim (str, s) {
  return trimLeft(trimRight(str, s), s);
}

function trimLeft (str, s) {
  s = s || ' ';
  while (str[0] === s) {
    str = str.slice(1);
  }
  return str;
}

function trimRight (str, s) {
  s = s || ' ';
  while (str[str.length - 1] === s) {
    str = str.slice(0, -1);
  }
  return str;
}

module.exports = Object.assign(trim, {
  left: trimLeft,
  right: trimRight,
});
