'use strict';

const _ = require('lodash');
const Router = require('./router');
const cml = require('cortml');

require('../proto');

module.exports = {

  loadFile (filename) {
    var self = this,
      data = cml.loadFile(filename),
      router = new Router(JSON.parse(cml.loadFile(filename).json()), {
        isAuth: false,
      });
    return router;
  },

};
