'use strict';

const Email = require('./email');

require('../proto');

module.exports = {

  Email: Email,

  emailText (opts) {
    var self = this,
      email = new Email(opts);
    return email.sendText(opts);
  },

  emailHtml (opts) {
    var self = this,
      email = new Email(opts);
    return email.sendHtml(opts);
  },

};
