'use strict';

const _ = require('plp/rextend')({}, [
  'test-search',
]);
const fs = require('fs');

global.plp = require('plp/lib/plp');

var p = global.p = plp.modules(v => [
  ['socket', 'link'].includes(v.type),
  v.isFile && (!['.js', '.node'].includes(v.ext)),
  ['legacy', 'lib', 'index.js', '.git'].includes(v.file),
  ['_', '-', '$'].includes(v.file[0]),
  v.meta.env === 'skip',
  !v.exists,
]);

console.log(p, p.length);
