'use strict';

/**
function appendElement(parent, child)

*/

const _ = {

};

const state = {

};

function appendElement (p = null, child = null) {
  var parent = typeof p === 'string'
      ? document.querySelector(p)
      : p;
  if (!parent || !child) return;
  parent.appendChild(child);
}

module.exports = appendElement;
