'use strict';

const _ = {
  each: require('./each'),
};

function exclude (data = {}, names = [], bindMethods = false) {
  var result = {};
  _.each(data, (val, name) => {
    if (names.includes(name)) return;
    if (bindMethods) val = val.bind(data);
    result[name] = val;
  });
  return result;
}

module.exports = exclude;
