function isCustom(element) {
  if (!element) return false;
  var tagName = typeof element === 'string' ? element : element.tagName;
  if (tagName && tagName.includes('-'))
    return true;
  let elem = document.createElement(tagName),
    isAttribute = elem.getAttribute('is');
  if (isAttribute === null)
    return false;
  return isAttribute.includes('-');
}

module.exports = isCustom;
