import './styles';

import dragdrop from './drag-drop';
import chooseFile from './choose-file';

import File from './file';
import FileSlice from './slice';

class FileKit {

  constructor () {
    var self = this;
    Object.assign(self, {
      File, FileSlice,
    });
  }

  chooseFile (...args) {
    var self = this;
    return chooseFile(...args);
  }

  draggable (...args) {
    return dragdrop.draggable(...args);
  }

  dropzone (...args) {
    return dragdrop.draggable(...args);
  }

}

export default new FileKit();
