import apply from './apply';
import attrs from 'util/domtools/attr';
import dataset from 'util/domtools/dataset';

class CortElement extends Element {

  attr (...args) {
    var self = this;
    return attrs(self, ...args);
  }

  removeAttr (name) {
    var self = this;
    self.removeAttribute(name);
    return self;
  }

  attrs () {
    var self = this;
    return self.attr();
  }

  ds (...args) {
    var self = this;
    return dataset(self, ...args);
  }

}

apply(CortElement);

export default CortElement;
