'use strict';

const _ = require('lodash');
const fs = require('fs');
const path = require('path');

const Project = require('./project');

module.exports = {

  load (dir, opts = {}) {
    return new Project(dir, opts);
  },

  build (dir, opts = {}) {
    var project = new Project(dir, opts);
    project.build();
    return project;
  },

};
