'use strict';

const _ = {
  create: require('../create'),
};

const state = {

};

function loadStyle (url) {
  return new Promise((resolve, reject) => {
    _.create({
      tag: 'link',
      $rel: 'stylesheet',
      $href: url,
      appendTo: 'head',
      events: {
        load (ev) {
          resolve(ev);
        },
        error (err) {
          reject(err);
        },
      },
    });
  });
}

module.exports = loadStyle;
