const _ = {
  each: require('./each'),
  flatten: require('./flatten'),
};

function parseClassName(...args) {
  var classes = _
      .flatten(args)
      .map(cl => cl.split(' ').map(s => s.split('.'))),
    result = new Set();
  _
    .flatten(classes)
    .filter(cl => !!(cl || '').trim())
    .forEach(cl => result.add(cl));
  return Array.from(result);
}

module.exports = parseClassName;
