'use strict';

const _ = {
  keys: require('./keys'),
  dasherize: require('./dasherize'),
};

function renderAttrs(data = {}) {
  return _
    .keys(data)
    .filter(key => data[key] !== null && data[key] !== undefined)
    .map(key => `${_.dasherize(key)}="${data[key]}"`)
    .join(' ');
}

module.exports = renderAttrs;
