'use strict';

function spliceFirst(arr, predicate = function () { return true; }, ...items) {
  if (typeof predicate !== 'function') {
    let originalPredicate = predicate;
    predicate = (val) => val === originalPredicate;
  }
  for (let i = 0, count = arr.length; i < count; i++) {
    if (predicate(arr[i]))
      return arr.splice(i, 1, ...items)[0];
  }
  return null;
}

module.exports = spliceFirst;
