'use strict';

function spliceLast(arr, predicate = function () { return true; }, ...items) {
  if (typeof predicate !== 'function') {
    let originalPredicate = predicate;
    predicate = (val) => val === originalPredicate;
  }
  for (let i = arr.length - 1, count = arr.length; i >= 0; i--) {
    if (predicate(arr[i]))
      return arr.splice(i, 1, ...items)[0];
  }
  return null;
}

module.exports = spliceLast;
