'use strict';

const _ = {

};

const state = {

};

function waitReady () {
  return new Promise((resolve, reject) => {
    if (document.readyState === 'complete' ||
      document.readyState === 'interactive') return resolve();
    document.addEventListener('DOMContentLoaded', ev => {
      resolve(ev);
    }, false);
  });
}

module.exports = waitReady;
