'use strict';

class Wrapper {

  constructor (name, cb) {
    var self = this;
    self.extend(self, {
      name,
      cb,
    });
  }

  one (data = {}, extend = {}) {
    var self = this;
    return self.extend(self.cb(data), extend);
  }

  many (datas = [], extend = {}) {
    var self = this;
    return datas.map(data => self.one(data, extend));
  }

}

module.exports = Wrapper;
