export default function Title() {
  const [text, setText] = useState('Welcome to the game');
  const [voiceIndex, setVoiceIndex] = useState(51); // 51 = Google UK English Male
  const onEnd = () => {
    // You could do something here after speaking has finished
  };
  const { speak, cancel, speaking, voices } = useSpeechSynthesis({
    onEnd,
  });

  const voice = voices[voiceIndex] || null;

  return (
      <div className="App">
      <h1 className="title">You Don't Know Diddly Squat</h1>
      <h3 className="title__sub">A multiplayer quiz show for the whole family!</h3>
      <Link to="/join"><button className="button">Let's Go!</button></Link>
      <form>
            <textarea
              id="message"
              name="message"
              rows={3}
              value={text}
              onChange={(event) => {
                setText(event.target.value);
              }}
            />
            {speaking ? (
              <button type="button" onClick={cancel}>
                Stop
              </button>
            ) : (
              <button
                type="button"
                onClick={() => speak({ text, voice })}
              >
                Speak
              </button>
            )}
      </form>
    </div>
  )
}
