import React, { useState, useEffect } from 'react';
import { useSpeechSynthesis } from 'react-speech-kit';
import { Link } from 'react-router-dom';
import LogoImage from "../../assets/Logo/LimboStudios.svg"
import '../../App.scss';
import './Logo.scss';

export default function Logo() {
  const [text, setText] = useState('Limbo Productions Presents');
  const { speak, voices } = useSpeechSynthesis();
  const voice = voices[51];

  const fadeOut = (item) => {
    const fadeTarget = document.querySelector(item)
    fadeTarget.style.opacity = '0'
    //   const fadeEffect = setInterval(function () {
  //     if (!fadeTarget.style.opacity) {
  //         fadeTarget.style.opacity = 1;
  //     }
  //     if (fadeTarget.style.opacity > 0) {
  //         fadeTarget.style.opacity -= 0.1;
  //     } else {
  //         clearInterval(fadeEffect);
  //     }
  // }, 200);
  }

  return (
      <div className="App">
        <img className="logo-image" src={LogoImage} alt="Limbo Productions Logo"/>        
        <Link to="/"><button className="button" onClick={() => {
          speak({ text, voice })
          fadeOut(".logo-image")
        }
        }
        >Click to start</button></Link>

      </div>

  )
}
