'use strict';

const _ = require('lodash');
const Util = require('./util');
const util = require('../util');

module.exports = class Keys extends Util {

  getParentKey (key) {
    var self = this;
    return key.slice(0, key.indexOf('.'));
  }

  getChildKey (key = '') {
    var self = this,
      parent = self._opts.key,
      prefix = parent ? `${parent}.` : '';
    return `${prefix}${key}`
  }

}
