'use strict';

const _ = require('lodash');
const path = require('path');
const kit = require('../lib');

kit.packer
  .packFile({
    context: path.resolve(__dirname, '..'),
    entry: 'client/index.js',
    output: {
      path: 'dist',
      filename: 'kit.js',
      library: 'kit',
    },
  })
  .then(() => console.log('Done'))
  .catch(err => console.log('ERR', err));
