// Attr Methods ================================================================

const Classes = [
  {
    proto: HTMLTableElement,
    attrs: ['border', 'cellpadding', 'cellspacing'],
  },
  {
    proto: HTMLInputElement,
    attrs: ['type', 'name', 'placeholder'],
  },
  {
    proto: HTMLButtonElement,
    attrs: ['type', 'name', 'placeholder'],
  },
  {
    proto: HTMLImageElement,
    attrs: ['src', 'alt'],
  },
  {
    proto: HTMLAnchorElement,
    attrs: ['href', 'target'],
  },
  {
    proto: HTMLTableCellElement,
    attrs: ['colspan', 'rowspan'],
  },
];

Classes.forEach(item => {
  item.attrs.forEach(attr => {
    // if (item.proto.prototype.hasOwnProperty(attr)) {
    //   return;
    // }
    Object.defineProperty(item.proto.prototype, attr, {
      get () {
        var self = this;
        return val => {
          self.setAttribute(attr, val);
          return self;
        };
      },
      set (val) {
        var self = this;
        self.setAttribute(attr, val);
        return self;
      },
    });
  });
});
