'use strict';

Object.assign(Element.prototype, {

  html (content) {
    var self = this;
    if (arguments.length === 0) {
      return self.innerHTML;
    }
    self.innerHTML = content;
    return self;
  },

  content (...args) {
    var self = this;
    return self.html(...args);
  },

});

Object.defineProperty(Element.prototype, '$', {
  get () {
    var self = this;
    return val => {
      self.html(val);
      return self;
    };
  },
  set (val) {
    var self = this;
    self.html(val);
    return self;
  },
});
