'use strict';

function flattenArray (arr) {
  var items = [];
  Array.from(arr, item => {
    if (!item || typeof item !== 'object') {
      return items.push(item);
    }
    if (item.length !== undefined) {
      item = Array.from(item);
    }
    items = items.concat(flattenArray(item));
  });
  return items;
}

module.exports = flattenArray;
