'use strict';

module.exports = class FunctionsCommon {

  constructor () {
    var self = this;
  }

  tick (handler, timeout = 0) {
    return new Promise((resolve, reject) => {
      setTimeout(() => {
        handler();
        resolve();
      }, timeout);
    });
  }

  injectMethods (target, source) {
    var self = this;
    Object.keys(source).forEach(name => {
      var method = source[name];
      if (typeof method !== 'function') {
        return;
      }
      target[name] = self.toMethod(method);
    });
  }

}
