'use strict';

const _ = require('lodash');
const Module = require('./module');
const Project = require('./project');

require('../proto');

module.exports = {
  Module,

  buildFile (filename, opts = {}) {
    var m = new Module(filename, opts);
    return m.renderFile();
  },

  createProject (root, opts = {}) {
    var self = this,
      project = new Project(root, opts);
    return project;
  },

  buildProject (root, opts = {}) {
    var self = this,
      project = self.createProject(root, opts);
    return project.build();
  },

  watchProject (root, opts = {}) {
    var self = this,
      project = self.createProject(root, opts);
    return project.watch();
  },

};
