function afterElement (t = null, child = null) {
  var target = typeof t === 'string'
      ? document.querySelector(t)
      : t,
    parent = target.parentElement,
    next = target.nextSibling;
  if (!target || !child) return;
  if (!next) return parent.appendChild(child);
  parent.insertBefore(child, next);
}

module.exports = afterElement;
