'use strict';

/**
function beforeElement(target, child)

*/

const _ = {

};

const state = {

};

function beforeElement (t = null, child = null) {
  var target = typeof t === 'string'
      ? document.querySelector(t)
      : t,
    parent = target.parentElement;
  if (!target || !child) return;
  parent.insertBefore(child, target);
}

module.exports = beforeElement;
