'use strict';

const _ = {
  capitalize: require('./capitalize'),
};

function camelCase (s, cap = false) {
  var res = s.replace(/([^a-zA-Z]+)([a-zA-Z])/g, (txt, splitter, letter) => {
      return `${letter.toUpperCase()}`;
    }),
    result = (res.slice(0, 1) || '').toLowerCase() + (res.slice(1) || '');
  return cap ? _.capitalize(result) : result;
}

module.exports = camelCase;
