'use strict';

function clearCode(code) {
  return code
    .replace(/^\s*\/\/.+/mgi, '')
    .replace(/\<\!\-\-[\s\S]+?\-\-\>/mgi, '')
    .replace(/\s+/mgi, ' ')
    .replace(/\>\s+\</mgi, '><')
    .replace(/\s+\>/mgi, '>')
    .replace(/\>\s+/mgi, '>')
    .replace(/\s+\</mgi, '<')
    .replace(/\<\s+/mgi, '<')
    ;
}

module.exports = clearCode;
