'use strict';

const _ = {
  extend: require('../extend'),
  createElement: require('./create-element'),
};

const state = {

};

function checkCreateArgs (args) {
  if (args.length > 2 && typeof args[0] === 'string') {
    return _.createElement(_.extend({
      tagName: args[0],
      className: args[1],
      html: typeof args[2] === 'string' ? args[2] : '',
      items: args[2] && Array.isArray(args[2]) ? args[2] : [],
    }, args[3] || {}));
  }
  if (args.length && args.filter(val => Array.isArray(val)).length === args.length)
    return args.map(opts => _.createElement(...opts));
  if (args.length > 1 && typeof args[0] !== 'string')
    return args.map(o => _.createElement(o));
  if (args.length === 1 && Array.isArray(args[0])) {
    return _.createElement({
      div: '',
      items: args[0],
    });
  }
  return undefined;
}

module.exports = checkCreateArgs;
