'use strict';

const _ = {
  pick: require('../pick'),
};

const state = {

};

function extractAttrsOpts (data = {}) {
  var predicate = (val, name) => {
      return name[0] === '$' && name[1] !== '$';
    },
    bindMethods = (val, name) => {
      return [name.slice(1), val];
    },
    opts = _.pick(data, predicate, bindMethods);
  return opts;
}

module.exports = extractAttrsOpts;
