'use strict';

const _ = {
  pick: require('../pick'),
};

const state = {

};

function extractEventsOptions (data = {}) {
  var predicate = (val, name) => {
      return name.startsWith('$_');
    },
    bindMethods = (val, name) => {
      return [name.slice(2), val];
    },
    opts = _.pick(data, predicate, bindMethods);
  return opts;
}

module.exports = extractEventsOptions;
