'use strict';

const _ = {

};

const state = {

};

function saveStyle (elem, name, force = false) {
  var value = window
    .getComputedStyle(elem)
    .getPropertyValue(name);
  elem.dataset[`_original_${name}`] = force
    ? value : elem.dataset[`_original_${name}`] || value;
  return elem;
}

module.exports = saveStyle;
