'use strict';

const _ = {
  create: require('./create'),
};

const state = {

};

function createClassDecorator(fn) {
  var args = [];

  return wrapper;

  function wrapper(...ar) {
    if (ar.length === 1 && typeof ar[0] === 'function') return fn(ar[0]);
    args = ar;
    return deco;
  }

  function deco(Class) {
    return fn(Class, ...args);
  }
}

module.exports = createClassDecorator;
