'use strict';

const _ = {

};

const state = {

};

function selfyDeco(Class) {
  var proto = Class.prototype;
  Object
    .getOwnPropertyNames(proto)
    .filter(name => !['constructor'].includes(name))
    .forEach(name => {
      var method = proto[name];
      if (typeof method !== 'function') return;
      proto[name] = function (...args) {
        var self = this;
        return method.call(self, self, ...args);
      };
    });
}

module.exports = selfyDeco;
