const _ = {
  uniqueId: require('./unique-id'),
};

const state = {
  name: '_plp_uid',
};

function elementUid(elem) {
  if (elem.dataset[state.name]) {
    return elem.dataset[state.name];
  }
  let uid = _.uniqueId('plp-elem-uid');
  elem.dataset[state.name] = uid;
  return uid;
}

export default elementUid;
