'use strict';

const _ = {
  _: require('../_'),
  keys: require('../keys'),
  capitalize: require('../capitalize'),
  extend: require('../extend'),

  getEmoji: require('./get-emoji'),
};

const state = {

};

function getSections (v = 'v1') {
  var index = _.extend({}, _.getEmoji(v));
  return _.keys(index).map(sName => _._({
    name: sName,
    title: _.capitalize(sName),
    parts: _.keys(index[sName]).map(pName => _._({
      name: pName,
      title: _.capitalize(pName),
      items: index[sName][pName].split(' ').map(s => s.trim()).filter(s => !!s),
    })),
  }));
}

module.exports = getSections;
