const _ = {
  each: require('./each'),
  set: require('./set'),
  get: require('./get'),
};

function extendUndefined(dest, ...sources) {
  _.each(sources, source => {
    _.each(source, (val, name) => {
      if (_.get(dest, name) !== undefined) return;
      _.set(dest, name, val);
    });
  });
  return dest;
}

export default extendUndefined;
