'use strict';

const _ = {
  isClass: require('./is-class'),
  getExtendedClass: require('./get-extended-class'),
};

const state = {

};

function getClassDescription (val) {
  var Class = _.isClass(val) ? val : _.getClass(val),
    Parent = _.getExtendedClass(Class),
    desc = `${Class.name}`;
  if (Parent !== Object) desc += ` extends ${Parent.name}`;
  return desc;
}

module.exports = getClassDescription;
